import { GoogleMap, MarkerF } from '@react-google-maps/api';
import { useEffect, useState } from 'react';
import { requestService } from './requestService';
import {
  ChatMessagePost,
  RequestGet,
  Status,
  Type,
  TypeEnum,
  User
} from './models';
import makeStyles from '@mui/styles/makeStyles/makeStyles';
import { useNavigate } from 'react-router-dom';
import { volunteerService } from './volunteerService';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';
import { userService } from './userService';
import { authService } from './authService';
import { RequestDrawer } from './RequestDrawer';

var stompClient = null;
let initialValue = {
  id: '',
  firstname: '',
  lastname: '',
  email: '',
  password: '',
  address: '',
  governmentIdentityId: '',
  profilePicture: ''
};
const headers = {
  Authorization: 'Bearer ' + authService.getToken()
};

export const Map = () => {
  const [latitude, setLatitude] = useState<number>();
  const [longitude, setLongitude] = useState<number>();
  const [requests, setRequests] = useState<RequestGet[]>([]);
  const [openSideBar, setOpenSideBar] = useState(false);
  const [user, setUser] = useState<User>(initialValue);

  const classes = useStyles();
  const navigate = useNavigate();

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLatitude(position.coords.latitude);
      setLongitude(position.coords.longitude);
    });
  }, []);

  useEffect(() => {
    connect();
    userService
      .getUserById(localStorage.getItem('userId'))
      .then((res) => res.data)
      .then((result) => setUser(result));
  }, []);

  useEffect(() => {
    requestService
      .getRequests()
      .then((res) => res.data)
      .then((result) => setRequests(result));
  }, []);

  const center = { lat: latitude, lng: longitude };

  const connect = () => {
    let Sock = new SockJS('http://localhost:8080/ws');
    stompClient = over(Sock);
    stompClient.connect(headers, onConnected, onError);
  };

  const onError = (err) => {
    console.log(err);
  };

  const onConnected = () => {
    if (stompClient.status === 'CONNECTED') {
      stompClient.subscribe('/chatroom/public', {});
      stompClient.subscribe(
        '/user/' + authService.getUserId() + '/private',
        {}
      );
    }
  };

  const sendPrivateValue = (event, request: RequestGet, message: string) => {
    event.preventDefault();
    if (stompClient) {
      var chatMessage: ChatMessagePost = {
        sender: user,
        recipient: request.requester,
        content: message,
        messageType: 'CHAT'
      };
      stompClient.send(
        '/myNeighborhood/private-message',
        headers,
        JSON.stringify(chatMessage)
      );
    }
    helpRequest(request.id);
  };

  const toggleDrawer = () => {
    setOpenSideBar((prev) => !prev);
  };

  const onClose = () => {
    setOpenSideBar(false);
  };

  const onOpen = () => {
    setOpenSideBar(true);
  };

  const helpRequest = (requestId: string) => {
    volunteerService
      .createVolunteer(requestId)
      .then(() => redirectChatboxPage());
  };

  return (
    <GoogleMap
      zoom={12}
      center={center}
      mapContainerStyle={{ width: '100%', height: '100vh' }}
    >
      {requests &&
        requests.map((request) => (
          <>
            <MarkerF
              key={request.id}
              position={{ lat: request.latitude, lng: request.longitude }}
              onClick={toggleDrawer}
              icon={
                TypeEnum[request.type] === TypeEnum.OneTimeTask
                  ? 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png'
                  : 'http://maps.google.com/mapfiles/ms/icons/yellow-dot.png'
              }
            />
            {openSideBar && (
              <RequestDrawer
                open={openSideBar}
                onClose={onClose}
                onOpen={onOpen}
                request={request}
                onSend={sendPrivateValue}
              />
            )}
          </>
        ))}
    </GoogleMap>
  );
};

const useStyles = makeStyles({
  paper: {
    width: 300,
    zIndex: '80 !important'
  },
  description: {
    width: '85%',
    marginTop: '25px'
  },
  fulfill: {
    marginTop: '30px'
  }
});
function redirectChatboxPage(): any {
  throw new Error('Function not implemented.');
}
