import { GoogleMap, MarkerF } from '@react-google-maps/api';
import { useEffect, useState } from 'react';
import { requestService } from './requestService';
import {
  ChatMessagePost,
  MessageType,
  RequestGet,
  Status,
  Type,
  User
} from './models';
import {
  Avatar,
  Box,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Drawer,
  Stack
} from '@mui/material';
import { Label } from './Label';
import { ButtonClick } from './ButtonClick';
import makeStyles from '@mui/styles/makeStyles/makeStyles';
import { useNavigate } from 'react-router-dom';
import { volunteerService } from './volunteerService';
import { TextInput } from './TextInput';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';
import { userService } from './userService';

var stompClient = null;
let initialValue = {
  id: '',
  firstname: '',
  lastname: '',
  email: '',
  password: '',
  address: '',
  governmentIdentityId: '',
  profilePicture: ''
};

export const Map = () => {
  const [latitude, setLatitude] = useState<number>();
  const [longitude, setLongitude] = useState<number>();
  const [requests, setRequests] = useState<RequestGet[]>([]);
  const [openSideBar, setOpenSideBar] = useState(false);
  const [openPopin, setOpenPopin] = useState(false);
  const [openMessage, setOpenMessage] = useState(true);
  const [message, setMessage] = useState('');
  const [user, setUser] = useState<User>(initialValue);

  const classes = useStyles();
  const navigate = useNavigate();

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLatitude(position.coords.latitude);
      setLongitude(position.coords.longitude);
    });
  }, []);

  useEffect(() => {
    connect();
    userService
      .getUserById(localStorage.getItem('userId'))
      .then((res) => res.data)
      .then((result) => setUser(result));
  });

  useEffect(() => {
    requestService
      .getRequests()
      .then((res) => res.data)
      .then((result) => setRequests(result));
  }, []);

  const center = { lat: latitude, lng: longitude };

  const connect = () => {
    let Sock = new SockJS('http://localhost:3000/ws');
    stompClient = over(Sock);
    stompClient.connect({}, onConnected, onError);
  };

  const onError = (err) => {
    console.log(err);
  };

  const onConnected = () => {
    stompClient.subscribe('/chatroom/public', {});
    stompClient.subscribe(
      '/user/' + localStorage.getItem('userId') + '/private',
      {}
    );
  };

  const sendPrivateValue = (event, request: RequestGet, message: string) => {
    event.preventDefault();
    if (stompClient) {
      var chatMessage: ChatMessagePost = {
        sender: user,
        recipient: request.requester,
        content: message,
        MessageType: MessageType.CHAT
      };
      stompClient.send('/myNeighborhood/private-message', {}, chatMessage);
    }
    helpRequest(request.id);
  };

  const toggleDrawer = () => {
    setOpenSideBar((prev) => !prev);
  };

  const disableMessage = () => {
    setOpenMessage(true);
  };

  const enableMessage = () => {
    setOpenMessage(false);
  };

  const handleClickOpen = () => {
    setOpenPopin(true);
  };

  const handleClose = () => {
    setOpenPopin(false);
    disableMessage();
  };

  const redirectChatboxPage = () => {
    navigate('/chatbox');
  };

  const handleMessage = (event) => {
    event.preventDefault();
    setMessage(event.target.value);
  };

  const helpRequest = (requestId: string) => {
    volunteerService
      .createVolunteer(requestId)
      .then(() => redirectChatboxPage());
  };

  return (
    <GoogleMap
      zoom={12}
      center={center}
      mapContainerStyle={{ width: '100%', height: '100vh' }}
    >
      {requests &&
        requests.map((request, index) => (
          <>
            <MarkerF
              key={index}
              position={{ lat: request.latitude, lng: request.longitude }}
              onClick={toggleDrawer}
            />
            <Drawer
              variant="persistent"
              anchor="left"
              open={openSideBar}
              onClose={() => setOpenSideBar(false)}
              classes={{ paper: classes.paper }}
            >
              <Box
                display="flex"
                justifyContent="center"
                alignItems="center"
                flexDirection="column"
                marginTop={'85px'}
              >
                <Stack direction="column">
                  <Avatar
                    alt="Requester profile"
                    src="/static/images/avatar/1.jpg"
                    sx={{
                      width: 86,
                      height: 86,
                      margin: 'auto'
                    }}
                  />
                  <h4>
                    {request.requester.firstname} {request.requester.lastname}
                  </h4>
                </Stack>
                <Stack spacing={2} direction="row">
                  <Label children={Type[request.type]} />
                  <Label children={Status[request.status]} />
                </Stack>
                <p className={classes.description}>{request.description}</p>
                <div className={classes.fulfill}>
                  <ButtonClick
                    backgroundColor="#FFA69E"
                    color="white"
                    borderRadius={12}
                    boxShadow
                    padding={12}
                    onClick={handleClickOpen}
                  >
                    Fulfill the need
                  </ButtonClick>
                  {openPopin && (
                    <Dialog open={openPopin} onClose={handleClose}>
                      <DialogTitle>One step from being a Hero !</DialogTitle>
                      <DialogContent>
                        <DialogContentText>
                          Do you confirm your willingness to help{' '}
                          {request.requester.firstname}{' '}
                          {request.requester.lastname} on his/her request? If
                          yes we invite you to send in the messages page
                        </DialogContentText>
                      </DialogContent>
                      <DialogContent>
                        <TextInput
                          label="Send your first message"
                          onChange={handleMessage}
                          value={message}
                          disabled={openMessage}
                          variant="filled"
                          fullwidth={true}
                        />
                      </DialogContent>
                      <DialogActions>
                        {openMessage ? (
                          <ButtonClick
                            backgroundColor="#FFA69E"
                            color="white"
                            onClick={enableMessage}
                          >
                            Let’s Help !
                          </ButtonClick>
                        ) : (
                          <ButtonClick
                            backgroundColor="#FFA69E"
                            color="white"
                            onClick={(event) =>
                              sendPrivateValue(event, request, message)
                            }
                          >
                            Send message
                          </ButtonClick>
                        )}
                        <ButtonClick
                          color="#FFA69E"
                          onClick={handleClose}
                          boxShadow={true}
                        >
                          Cancel
                        </ButtonClick>
                      </DialogActions>
                    </Dialog>
                  )}
                </div>
              </Box>
            </Drawer>
          </>
        ))}
    </GoogleMap>
  );
};

const useStyles = makeStyles({
  paper: {
    width: 300,
    zIndex: '80 !important'
  },
  description: {
    width: '85%',
    marginTop: '25px'
  },
  fulfill: {
    marginTop: '30px'
  }
});
