import { Status, Type } from './models';

type LabelType = {
  value: Type | Status;
};

type BackgroundColorType = {
  [key: Type | Status]: string;
};

export const Label = ({ value }: LabelType) => {
  const backgroundColor: BackgroundColorType = {};
  (backgroundColor[Type.OneTimeTask] = '00A0A0'),
    (backgroundColor[Type.MaterialNeed] = 'FDEE00'),
    (backgroundColor[Status.Fulfilled] = '32CD32'),
    (backgroundColor[Status.Unfulfilled] = 'E73131');

  return (
    <span
      style={{
        borderRadius: '12px',
        padding: '8px',
        backgroundColor: backgroundColor
      }}
    >
      {value}
    </span>
  );
};
