type InputFileProps = {
  accept: string;
};

export const InputFile = ({ accept }: InputFileProps) => {
  const classes = useStyles();

  return (
    <Button className={classes.inputFile}>
      <input hidden accept={accept} type="file" />
    </Button>
  );
};

const useStyles = makeStyles({
  inputFile: {},
});
