import {
  FormControl,
  InputLabel,
  MenuItem,
  Paper,
  Select,
  SelectChangeEvent,
  Stack
} from '@mui/material';
import makeStyles from '@mui/styles/makeStyles';
import { Header } from './Header';
import { ChangeEvent, useEffect, useState } from 'react';
import { Type } from './models';
import { TextInput } from './TextInput';
import setBodyColor from './setBody';
import { Request } from './models';
import { ButtonClick } from './ButtonClick';
import { useNavigate } from 'react-router-dom';
import { requestPost } from './requestApi';

export const CreateRequest = () => {
  const classes = useStyles();
  const navigate = useNavigate();

  const initialValue = {
    type: Type.OneTimeTask,
    latitude: null,
    longitude: null,
    description: ''
  };

  const [request, setRequest] = useState<Request>(initialValue);

  setBodyColor({ color: '#DDFFF7' });

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setRequest({ ...request, latitude: position.coords.latitude });
      setRequest({ ...request, longitude: position.coords.longitude });
    });
  }, []);

  const isValid =
    !!request.description && !!request.latitude && !!request.longitude;

  const handleTypeChange = (event: SelectChangeEvent) => {
    setRequest({ ...request, type: event.target.value as Type });
  };

  const handleDescriptionChange = (event: ChangeEvent<HTMLInputElement>) => {
    setRequest({ ...request, description: event.target.value });
  };

  const redirectHomePage = () => {
    navigate('/');
  };

  const onSubmit = (e: SubmitEvent) => {
    e.preventDefault();
    requestPost(request, redirectHomePage);
  };

  return (
    <div>
      <Header />
      <Paper elevation={2} className={classes.form}>
        <h1 className={classes.title}>Create Request</h1>
        <Stack
          spacing={5}
          direction="row"
          className={classes.formContainer}
          useFlexGap
          flexWrap="wrap"
        >
          <FormControl>
            <InputLabel id="select-request-type">
              Type of the request
            </InputLabel>
            <Select
              labelId="select-request-type"
              value={request.type}
              label="Type of the request"
              onChange={handleTypeChange}
            >
              <MenuItem value={Type.OneTimeTask}>{Type.OneTimeTask}</MenuItem>
              <MenuItem value={Type.MaterialNeed}>{Type.MaterialNeed}</MenuItem>
            </Select>
          </FormControl>
          <TextInput label="Location" onChange={() => {}} />
          <TextInput
            label="Description"
            onChange={handleDescriptionChange}
            type="text"
            placeholder="Description of the request (300 characters maximum)"
            rows={8}
            fullwidth={true}
            multiline={true}
            maxLength={300}
          />
        </Stack>
        <Stack
          spacing={2}
          direction="row"
          justifyContent="center"
          className={classes.submitButtonStack}
        >
          <ButtonClick
            type="submit"
            variant="contained"
            backgroundColor="#FFA69E"
            size="large"
            onClick={onSubmit}
            disabled={!isValid}
          >
            Create request
          </ButtonClick>
        </Stack>
      </Paper>
    </div>
  );
};

const useStyles = makeStyles({
  title: {
    display: 'flex',
    justifyContent: 'center'
  },
  form: {
    width: '600px',
    margin: 'auto',
    marginTop: '150px',
    padding: '60px'
  },
  formContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  submitButtonStack: {
    marginTop: '18px'
  }
});
