import { makeStyles } from "@mui/styles";
import CloudUpload from "@mui/icons-material/CloudUpload";
import CloudDoneIcon from "@mui/icons-material/CloudDone";
import { useState } from "react";

type InputFileProps = {
  accept: string;
  file: string;
  onChange: (value) => void;
};

export const InputFile = ({ accept, file, onChange }: InputFileProps) => {
  const classes = useStyles();
  const [fileName, setFileName] = useState("No selected file");

  return (
    <form
      action=""
      style={{ backgroundColor: "#93E1D8" }}
      className={classes.inputFile}
      onClick={() => {
        let element: HTMLElement = document.getElementsByClassName(
          "input-field"
        )[0] as HTMLElement;
        element.click();
      }}
    >
      <input
        accept={accept}
        type="file"
        className="input-field"
        hidden
        onChange={({ target: { files } }) => {
          files[0] && setFileName(files[0].name);
          if (files) {
            onChange(URL.createObjectURL(files[0]));
          }
        }}
      />
      {file ? (
        <div className={classes.state}>
          <CloudDoneIcon className={classes.icon} sx={{ fontSize: 60 }} />
          <span>{file}</span>
        </div>
      ) : (
        <div className={classes.state}>
          <CloudUpload className={classes.icon} sx={{ fontSize: 60 }} />
          <span>Browse a file to upload</span>
        </div>
      )}
    </form>
  );
};

const useStyles = makeStyles({
  inputFile: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    cursor: "pointer",
    height: "200px",
    width: "300px",
    border: "3px dashed #FFA69E",
    borderRadius: "5px",
  },
  state: {
    display: "flex",
    flexDirection: "column",
  },
  icon: {
    display: "flex",
    alignItems: "center",
  },
});
