import { GoogleMap, MarkerF } from '@react-google-maps/api';
import { useEffect, useState } from 'react';
import { requestService } from './requestService';
import { Request } from './models';
import { Avatar, Box, Drawer, Stack } from '@mui/material';
import { Label } from './Label';
import { Status, Type } from './models';
import { ButtonClick } from './ButtonClick';
import makeStyles from '@mui/styles/makeStyles/makeStyles';

export const Map = () => {
  const [latitude, setLatitude] = useState<number>();
  const [longitude, setLongitude] = useState<number>();
  const [requests, setRequests] = useState<Request[]>([]);
  const [openSideBar, setOpenSideBar] = useState(false);

  const classes = useStyles();

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLatitude(position.coords.latitude);
      setLongitude(position.coords.longitude);
    });
  }, []);

  useEffect(() => {
    requestService
      .getRequests()
      .then((res) => res.data)
      .then((result) => setRequests(result));
  }, []);

  const center = { lat: latitude, lng: longitude };

  const toggleDrawer = () => {
    setOpenSideBar((prev) => !prev);
  };

  return (
    <GoogleMap
      zoom={12}
      center={center}
      mapContainerStyle={{ width: '100%', height: '100vh' }}
    >
      {requests.map((request, index) => (
        <MarkerF
          key={index}
          position={{ lat: request.latitude, lng: request.longitude }}
          onClick={toggleDrawer}
        />
      ))}

      <Drawer
        variant="persistent"
        anchor="left"
        open={openSideBar}
        onClose={() => setOpenSideBar(false)}
        classes={{ paper: classes.paper }}
      >
        <Box
          display="flex"
          justifyContent="center"
          alignItems="center"
          flexDirection="column"
          marginTop={'85px'}
        >
          <Stack direction="column">
            <Avatar
              alt="Requester profile"
              src="/static/images/avatar/1.jpg"
              sx={{
                width: 86,
                height: 86,
                margin: 'auto'
              }}
            />
            <h4>Firstname Lastname</h4>
          </Stack>
          <Stack spacing={2} direction="row">
            <Label children={Type.OneTimeTask} />
            <Label children={Status.Unfulfilled} />
          </Stack>
          <p className={classes.description}>
            Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean
            commodo ligula eget dolor. Aenean massa. Cum sociis natoque
            penatibus et magnis dis parturient montes, nascetur ridiculus mus.
            Donec quam felis, ultricies nec, pellentesque eu, pretium quis, sem.
            Nulla consequat massa quis enim. Donec.
          </p>
          <div className={classes.fulfill}>
            <ButtonClick
              backgroundColor="#FFA69E"
              color="white"
              borderRadius={12}
              boxShadow
              padding={12}
            >
              Fulfill the need
            </ButtonClick>
          </div>
        </Box>
      </Drawer>
    </GoogleMap>
  );
};

const useStyles = makeStyles({
  paper: {
    width: 300,
    zIndex: '80 !important'
  },
  description: {
    width: '85%',
    marginTop: '25px'
  },
  fulfill: {
    marginTop: '30px'
  }
});
