import { GoogleMap } from '@react-google-maps/api';
import { useEffect, useState } from 'react';
import { requestService } from './requestService';
import { ChatMessagePost, MessageType, RequestGet, User } from './models';
import { useNavigate } from 'react-router-dom';
import { volunteerService } from './volunteerService';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';
import { userService } from './userService';
import { authService } from './authService';
import { RequestMarker } from './RequestMarker';
import { headers } from './apiModels';

var stompClient = null;
let initialValue = {
  id: '',
  firstname: '',
  lastname: '',
  email: '',
  password: '',
  address: '',
  governmentIdentityId: '',
  profilePictureId: ''
};

export const Map = () => {
  const [latitude, setLatitude] = useState<number>();
  const [longitude, setLongitude] = useState<number>();
  const [requests, setRequests] = useState<RequestGet[]>([]);
  const [user, setUser] = useState<User>(initialValue);

  const navigate = useNavigate();

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLatitude(position.coords.latitude);
      setLongitude(position.coords.longitude);
    });
  }, []);

  useEffect(() => {
    connect();
    userService
      .getUserById(localStorage.getItem('userId'))
      .then((res) => res.data)
      .then((result) => setUser(result));
  }, []);

  useEffect(() => {
    requestService
      .getRequests()
      .then((res) => res.data)
      .then((result) => setRequests(result));
  }, []);

  const center = { lat: latitude, lng: longitude };

  const connect = () => {
    let Sock = new SockJS('http://localhost:8080/ws');
    stompClient = over(Sock);
    stompClient.connect({}, onConnected, onError);
  };

  const onError = (err) => {
    console.log(err);
  };

  const onConnected = () => {
    if (stompClient.status === 'CONNECTED') {
      stompClient.subscribe('/chatroom/public', {});
      stompClient.subscribe(
        '/user/' + authService.getUserId() + '/private',
        {}
      );
    }
  };

  const sendPrivateValue = (event, request: RequestGet, message: string) => {
    event.preventDefault();
    if (stompClient) {
      var chatMessage: ChatMessagePost = {
        sender: user,
        recipient: request.requester,
        content: message,
        messageType: MessageType.CHAT
      };
      stompClient.send(
        '/myNeighborhood/private-message',
        headers,
        JSON.stringify(chatMessage)
      );
    }
    helpRequest(request.id);
  };

  const helpRequest = (requestId: string) => {
    volunteerService
      .createVolunteer(requestId)
      .then(() => redirectChatboxPage());
  };

  const redirectChatboxPage = () => {
    navigate('/chatbox');
  };

  return (
    <GoogleMap
      zoom={12}
      center={center}
      mapContainerStyle={{ width: '100%', height: '100vh' }}
    >
      {requests &&
        requests.map((request, index) => (
          <RequestMarker
            key={index}
            request={request}
            onSend={sendPrivateValue}
          />
        ))}
    </GoogleMap>
  );
};
