import { makeStyles } from "@mui/styles";
import CloudUpload from "@mui/icons-material/CloudUpload";

type InputFileProps = {
  accept: string;
};

export const InputFile = ({ accept }: InputFileProps) => {
  const classes = useStyles();

  return (
    <form onClick={() => document.querySelector("input-field")}>
      <input accept={accept} type="file" className="input-field" />
    </form>
  );
};

const useStyles = makeStyles({
  form: {
    height: "100px",
    width: "200px",
    backgourndColor: "#93E1D8",
  },
});
