import { makeStyles } from "@mui/styles";
import CloudUpload from "@mui/icons-material/CloudUpload";
import CloudDoneIcon from "@mui/icons-material/CloudDone";
import DeleteIcon from "@mui/icons-material/Delete";
import { useState } from "react";
import { Stack } from "@mui/material";

type InputFileProps = {
  accept: string;
  file?: string | null;
  onChange: (value) => void;
};

export const InputFile = ({ accept, file, onChange }: InputFileProps) => {
  const classes = useStyles();
  const [fileName, setFileName] = useState("No selected file");

  return (
    <>
      <form
        action=""
        style={
          file ? { backgroundColor: "#23DC3D" } : { backgroundColor: "#93E1D8" }
        }
        className={classes.inputFile}
        onClick={() => {
          let element: HTMLElement = document.getElementsByClassName(
            "input-field"
          )[0] as HTMLElement;
          element.click();
        }}
      >
        <input
          accept={accept}
          type="file"
          className="input-field"
          hidden
          onChange={({ target: { files } }) => {
            files[0] && setFileName(files[0].name);
            if (files) {
              onChange(URL.createObjectURL(files[0]));
            }
          }}
        />
        {file ? (
          <Stack direction="column" alignItems="center" gap={1}>
            <CloudDoneIcon sx={{ fontSize: 60 }} />
            <span>{fileName}</span>
          </Stack>
        ) : (
          <Stack direction="column" alignItems="center" gap={1}>
            <CloudUpload sx={{ fontSize: 60 }} />
            <span>Browse a file to upload</span>
          </Stack>
        )}
      </form>
      <span>
        {fileName} <DeleteIcon />
      </span>
    </>
  );
};

const useStyles = makeStyles({
  inputFile: {
    display: "flex",
    textAlign: "center",
    justifyContent: "center",
    alignItems: "center",
    cursor: "pointer",
    height: "200px",
    width: "300px",
    border: "3px dashed #FFA69E",
    borderRadius: "5px",
  },
});
