import { Paper, Stack } from '@mui/material';
import { Header } from './Header';
import { InputFile } from './InputFile';
import { makeStyles } from '@mui/styles';
import { TextInput } from './TextInput';
import setBodyColor from './setBody';
import { ButtonClick } from './ButtonClick';
import { ChangeEvent, useState } from 'react';
import { RegisterRequest } from './models';
import { authRequestRegister } from './authRequestApi';
import { useNavigate } from 'react-router-dom';
import { governmentIdentityService } from './governmentIdentityService';

export const SignUp = () => {
  const classes = useStyles();
  const navigate = useNavigate();

  setBodyColor({ color: '#DDFFF7' });

  const initialValue = {
    firstname: '',
    lastname: '',
    email: '',
    password: '',
    address: '',
    governmentIdentityId: ''
  };

  const [user, setUser] = useState<RegisterRequest>(initialValue);
  const [governmentIdentity, setGovernmentIdentity] = useState<File>();

  const isValid =
    !!user.firstname &&
    !!user.lastname &&
    !!user.address &&
    !!user.address &&
    !!user.email &&
    !!user.password;

  const handleFirstname = (event: ChangeEvent<HTMLInputElement>) => {
    event.preventDefault();
    setUser({ ...user, firstname: event.target.value });
  };

  const handleLastname = (event: ChangeEvent<HTMLInputElement>) => {
    event.preventDefault();
    setUser({ ...user, lastname: event.target.value });
  };

  const handleEmail = (event: ChangeEvent<HTMLInputElement>) => {
    event.preventDefault();
    setUser({ ...user, email: event.target.value });
  };

  const handlePassword = (event: ChangeEvent<HTMLInputElement>) => {
    event.preventDefault();
    setUser({ ...user, password: event.target.value });
  };

  const handleAddress = (event: ChangeEvent<HTMLInputElement>) => {
    event.preventDefault();
    setUser({ ...user, address: event.target.value });
  };

  const redirectHomePage = () => {
    navigate('/');
  };

  const onSubmit = (e: SubmitEvent) => {
    e.preventDefault();
    authRequestRegister(user, redirectHomePage);
  };

  const sendGovernmentIdentity = (event) => {
    event.preventDefault();
    governmentIdentityService.uploadGovernmentIdentity(governmentIdentity);
  };

  return (
    <div>
      <Header />
      <Paper elevation={2} className={classes.form}>
        <h1 className={classes.title}>Registration form</h1>
        <Stack spacing={2} direction="row" className={classes.formContainer}>
          <Stack spacing={2} direction="column">
            <TextInput
              label="Firstname"
              type="text"
              onChange={handleFirstname}
            />
            <TextInput label="Lastname" type="text" onChange={handleLastname} />
            <TextInput label="Email" type="email" onChange={handleEmail} />
            <TextInput
              label="Password"
              type="password"
              onChange={handlePassword}
            />
          </Stack>
          <Stack spacing={2} direction="column">
            <TextInput label="Address" type="text" onChange={handleAddress} />
            <InputFile
              file={governmentIdentity}
              setFile={setGovernmentIdentity}
              accept="image/*"
              onChange={sendGovernmentIdentity}
            />
          </Stack>
        </Stack>
        <Stack
          className={classes.submitButtonStack}
          spacing={2}
          direction="row"
          justifyContent="center"
        >
          <ButtonClick
            type="submit"
            variant="contained"
            backgroundColor="#FFA69E"
            size="large"
            onClick={onSubmit}
            disabled={!isValid}
          >
            Sumbit
          </ButtonClick>
        </Stack>
      </Paper>
    </div>
  );
};

const useStyles = makeStyles({
  title: {
    display: 'flex',
    justifyContent: 'center'
  },
  form: {
    width: '600px',
    margin: 'auto',
    marginTop: '150px',
    padding: '60px'
  },
  formContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  submitButtonStack: {
    marginTop: '12px'
  }
});
