import { GoogleMap, MarkerF } from '@react-google-maps/api';
import { useEffect, useState } from 'react';
import { requestService } from './requestService';
import { RequestGet, Status, Type } from './models';
import {
  Avatar,
  Box,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Drawer,
  Stack
} from '@mui/material';
import { Label } from './Label';
import { ButtonClick } from './ButtonClick';
import makeStyles from '@mui/styles/makeStyles/makeStyles';
import { useNavigate } from 'react-router-dom';
import { volunteerService } from './volunteerService';
import { TextInput } from './TextInput';

export const Map = () => {
  const [latitude, setLatitude] = useState<number>();
  const [longitude, setLongitude] = useState<number>();
  const [requests, setRequests] = useState<RequestGet[]>([]);
  const [openSideBar, setOpenSideBar] = useState(false);
  const [openPopin, setOpenPopin] = useState(false);
  const [openMessage, setOpenMessage] = useState(true);
  const [message, setMessage] = useState('');

  const classes = useStyles();
  const navigate = useNavigate();

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      setLatitude(position.coords.latitude);
      setLongitude(position.coords.longitude);
    });
  }, []);

  useEffect(() => {
    console.log(openMessage);
    requestService
      .getRequests()
      .then((res) => res.data)
      .then((result) => setRequests(result));
  }, []);

  const center = { lat: latitude, lng: longitude };

  const toggleDrawer = () => {
    setOpenSideBar((prev) => !prev);
  };

  const disableMessage = () => {
    setOpenMessage(true);
  };

  const enableMessage = () => {
    setOpenMessage(false);
  };

  const handleClickOpen = () => {
    setOpenPopin(true);
    enableMessage();
  };

  const handleClose = () => {
    setOpenPopin(false);
  };

  const redirectChatboxPage = () => {
    navigate('/chatbox');
  };

  const handleMessage = (event) => {
    event.preventDefault();
    setMessage(event.target.value);
  };

  const helpRequest = (event, requestId: string) => {
    event.preventDefault();
    volunteerService
      .createVolunteer(requestId)
      .then(() => redirectChatboxPage());
  };

  return (
    <GoogleMap
      zoom={12}
      center={center}
      mapContainerStyle={{ width: '100%', height: '100vh' }}
    >
      {requests &&
        requests.map((request, index) => (
          <>
            <MarkerF
              key={index}
              position={{ lat: request.latitude, lng: request.longitude }}
              onClick={toggleDrawer}
            />
            <Drawer
              variant="persistent"
              anchor="left"
              open={openSideBar}
              onClose={() => setOpenSideBar(false)}
              classes={{ paper: classes.paper }}
            >
              <Box
                display="flex"
                justifyContent="center"
                alignItems="center"
                flexDirection="column"
                marginTop={'85px'}
              >
                <Stack direction="column">
                  <Avatar
                    alt="Requester profile"
                    src="/static/images/avatar/1.jpg"
                    sx={{
                      width: 86,
                      height: 86,
                      margin: 'auto'
                    }}
                  />
                  <h4>
                    {request.requester.firstname} {request.requester.lastname}
                  </h4>
                </Stack>
                <Stack spacing={2} direction="row">
                  <Label children={Type[request.type]} />
                  <Label children={Status[request.status]} />
                </Stack>
                <p className={classes.description}>{request.description}</p>
                <div className={classes.fulfill}>
                  <ButtonClick
                    backgroundColor="#FFA69E"
                    color="white"
                    borderRadius={12}
                    boxShadow
                    padding={12}
                    onClick={handleClickOpen}
                  >
                    Fulfill the need
                  </ButtonClick>
                  {openPopin && (
                    <Dialog
                      open={openPopin}
                      onClose={handleClose}
                      onBlur={enableMessage}
                    >
                      <DialogTitle>One step from being a Hero !</DialogTitle>
                      <DialogContent>
                        <DialogContentText>
                          Do you confirm your willingness to help{' '}
                          {request.requester.firstname}{' '}
                          {request.requester.lastname} on his/her request? If
                          yes we invite you to send in the messages page
                        </DialogContentText>
                      </DialogContent>
                      <TextInput
                        label="Send your first message"
                        onChange={handleMessage}
                        value={message}
                        disabled={openMessage}
                      />
                      <DialogActions>
                        <ButtonClick
                          backgroundColor="#FFA69E"
                          color="white"
                          onClick={disableMessage}
                        >
                          Let’s Help !
                        </ButtonClick>
                        <ButtonClick
                          color="#FFA69E"
                          onClick={handleClose}
                          boxShadow={true}
                        >
                          Cancel
                        </ButtonClick>
                      </DialogActions>
                    </Dialog>
                  )}
                </div>
              </Box>
            </Drawer>
          </>
        ))}
    </GoogleMap>
  );
};

const useStyles = makeStyles({
  paper: {
    width: 300,
    zIndex: '80 !important'
  },
  description: {
    width: '85%',
    marginTop: '25px'
  },
  fulfill: {
    marginTop: '30px'
  }
});
