import { Header } from './Header';
import { CSSObject, Theme, styled } from '@mui/material/styles';
import MuiDrawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import { useEffect, useState } from 'react';
import makeStyles from '@mui/styles/makeStyles/makeStyles';
import { useRedirectToSignInPage } from './useRedirectToSignInPage';
import { RecipientItem } from './RecipientItem';
import { Box, InputAdornment } from '@mui/material';
import { ButtonClick } from './ButtonClick';
import { ChatMessage } from './models';
import SockJs from 'sockjs-client';
import { over } from 'stompjs';
import { TextInput } from './TextInput';

const drawerWidth = 240;
var stompClient = null;

const DrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'flex-end',
  padding: theme.spacing(0, 1),
  ...theme.mixins.toolbar
}));

const openedMixin = (theme: Theme): CSSObject => ({
  width: drawerWidth,
  transition: theme.transitions.create('width', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.enteringScreen
  }),
  overflowX: 'hidden'
});

const closedMixin = (theme: Theme): CSSObject => ({
  transition: theme.transitions.create('width', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen
  }),
  overflowX: 'hidden',
  width: `calc(${theme.spacing(7)} + 1px)`,
  [theme.breakpoints.up('sm')]: {
    width: `calc(${theme.spacing(8)} + 1px)`
  }
});

const Drawer = styled(MuiDrawer, {
  shouldForwardProp: (prop) => prop !== 'open'
})(({ theme, open }) => ({
  width: drawerWidth,
  flexShrink: 0,
  whiteSpace: 'nowrap',
  boxSizing: 'border-box',
  ...(open && {
    ...openedMixin(theme),
    '& .MuiDrawer-paper': openedMixin(theme)
  }),
  ...(!open && {
    ...closedMixin(theme),
    '& .MuiDrawer-paper': closedMixin(theme)
  })
}));

export const Chatbox = () => {
  const classes = useStyles();
  useRedirectToSignInPage();

  const [open, setOpen] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [userData, setUserData] = useState<ChatMessage>(null);
  //const [privateChats, setPrivateChats] = useState([]);
  const [tab, setTab] = useState('');

  useEffect(() => {
    registerUser();
  }, []);

  const handleDrawer = () => {
    setOpen(!open);
  };

  const handleMessage = (event) => {
    const { value } = event.target;
    setUserData({ ...userData, content: value });
  };

  const registerUser = () => {
    let Sock = new SockJs('http://localhost:3000/ws');
    stompClient = over(Sock);
    stompClient.connect({}, onConnected, onError);
  };

  const handleTab = (name: string, key: number) => {
    setTab(name);
    setSelectedIndex(key);
  };

  /*const onConnected = () => {
    setUserData({ ...userData, connected: true });
    stompClient.subscribe(
      '/user/' + userData.userId + '/private'
      onPrivateMessageReceived
    );
  };*/

  const onError = (error) => {
    console.log(error);
  };

  /*const onPrivateMessageReceived = (payload) => {
    let payloadData = JSON.parse(payload.body);
    if (privateChats.get(payloadData.senderName)) {
      privateChats.get(payloadData.senderName).push(payloadData);
      setPrivateChats(new Map(privateChats));
    } else {
      let list = [];
      list.push(payloadData);
      privateChats.get(payloadData.senderName).push(payloadData);
      setPrivateChats(new Map(privateChats));
    }
  };*/

  const sendPrivateMessage = () => {
    if (stompClient) {
      let chatMessage = {
        senderId: userData.sender.id,
        recipientId: tab,
        content: userData.content,
        status: 'CHAT'
      };
      stompClient.send(
        '/myNeighborhood/privateMessage',
        {},
        JSON.stringify(chatMessage)
      );
      setUserData({ ...userData, content: chatMessage.content });
    }
  };

  return (
    <div>
      <Header />
      <Drawer
        variant="permanent"
        open={open}
        anchor="left"
        classes={{ paper: classes.paper }}
      >
        <div className={classes.content}>
          <DrawerHeader>
            <IconButton onClick={handleDrawer}>
              {open ? <ChevronLeftIcon /> : <ChevronRightIcon />}
            </IconButton>
          </DrawerHeader>
          <Divider />
          <List>
            {['Inbox', 'Starred', 'Send email', 'Drafts'].map((text, key) => (
              <RecipientItem
                key={key}
                primary={text}
                secondary={'sous text'}
                open={open}
                onClick={(value) => handleTab(value, key)}
                selected={selectedIndex === key}
              />
            ))}
          </List>
        </div>
      </Drawer>
      <Box className={classes.conversation}>
        <div className={classes.messageFlow}></div>
        <TextInput
          placeholder="Type your message here..."
          type="text"
          variant="outlined"
          onChange={handleMessage}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <ButtonClick
                  type="submit"
                  variant="contained"
                  backgroundColor="#FFA69E"
                  onClick={sendPrivateMessage}
                >
                  Send
                </ButtonClick>
              </InputAdornment>
            )
          }}
          label={''}
        />
      </Box>
    </div>
  );
};

const useStyles = makeStyles({
  paper: {
    zIndex: '80 !important'
  },
  content: {
    marginTop: '65px'
  },
  conversation: {
    display: 'flex',
    flexDirection: 'column',
    flexGrow: 1,
    margin: '80px',
    zIndex: '80 !important'
  },
  messageFlow: {
    flexGrow: 1,
    overflow: 'auto'
  },
  avatar: {},
  messageData: {}
});
